$(function() {
    var e = $(".GoTopSmall.Fixed"),
        t = Math.max(document.body.scrollHeight, document.documentElement.scrollHeight, document.body.offsetHeight, document.documentElement.offsetHeight, document.body.clientHeight, document.documentElement.clientHeight),
        o = document.documentElement.clientHeight,
        n = document.documentElement.clientWidth;
    $(window).resize(function() {
        t = Math.max(document.body.scrollHeight, document.documentElement.scrollHeight, document.body.offsetHeight, document.documentElement.offsetHeight, document.body.clientHeight, document.documentElement.clientHeight), o = document.documentElement.clientHeight, n = document.documentElement.clientWidth
    }), $(window).scroll(function() {
        var c = window.pageYOffset || document.documentElement.scrollTop;
        c > 500 && c < t - o - 300 ? e.fadeIn() : (c <= 500 || c > t - o - 300 && n < 767) && e.fadeOut()
    }), $(".GoTop, .GoTopSmall").click(function() {
        $("html,body,#root").animate({
            scrollTop: 0
        }, 500)
    })
});