$(function() {
    $(".mfp-close").click(function() {
        $.magnificPopup.close()
    }), $(".seo-review .Text").click(function() {
        i($(this).closest(".seo-review").data("id"))
    });
    var e = [];

    function i(e) {
        setTimeout(function() {
            $.magnificPopup.open({
                items: {
                    src: "#seo_review_popup_" + e
                },
                type: "inline"
            })
        }, 1)
    }
    $(".seo-review").each(function(i, t) {
        e.push($(t).data("id"))
    }), $(".seo-review-popup .Arrow").click(function() {
        var t = e.indexOf($(this).closest(".seo-review-popup").data("id"));
        switch ($(this).data("direction")) {
            case "left":
                i(0 === t ? e[e.length - 1] : e[t - 1]);
                break;
            case "right":
                i(t === e.length - 1 ? e[0] : e[t + 1])
        }
    })
});